tinymce.PluginManager.add('vizus_image', function(editor, url)
{
	function createDialog()
	{
		var url = vizus.url.replace(document.location.toString(), {query: {cmd: 'cms:editor.tinymce.plugins.image'}});
		vizus.cms.dialog.open(url, 0, 0, null, {editor: editor});
	}
	
	tinymce.util.I18n.add('cs',
	{
		'Insert/edit image': 'Vložit/změnit obrázek',
		'Insert image': 'Vložit obrázek',
	});

	editor.addButton('vizus_image',
	{
		icon: 'image',
		tooltip: 'Insert/edit image',
		onclick: createDialog,
		stateSelector: 'img[data-vizus-cms-component="editor.image"]'
	});

	editor.addMenuItem('vizus_image',
	{
		icon: 'image',
		text: 'Insert image',
		onclick: createDialog,
		context: 'insert',
		prependToContext: true,
		stateSelector: 'img[data-vizus-cms-component="editor.image"]'
	});
});
