tinymce.PluginManager.add('vizus_flash', function(editor, url)
{
	function createDialog()
	{
		var url = vizus.url.replace(document.location.toString(), {query: {cmd: 'cms:editor.tinymce.plugins.flash'}});
		vizus.cms.dialog.open(url, 0, 0, null, {editor: editor});
	}
	
	tinymce.util.I18n.add('cs',
	{
		'Insert/edit flash': 'Vložit/změnit flash',
		'Insert flash': 'Vložit flash',
	});

	editor.addButton('vizus_flash',
	{
		icon: 'media',
		tooltip: 'Insert/edit flash',
		onclick: createDialog,
		stateSelector: 'div[data-vizus-cms-component="editor.flash"]'
	});

	editor.addMenuItem('vizus_flash',
	{
		icon: 'media',
		text: 'Insert flash',
		onclick: createDialog,
		context: 'insert',
		prependToContext: true,
		stateSelector: 'div[data-vizus-cms-component="editor.flash"]'
	});
});
