tinymce.PluginManager.add('vizus_html', function(editor, url)
{
	function createDialog()
	{
		var url = vizus.url.replace(document.location.toString(), {query: {cmd: 'cms:editor.tinymce.plugins.html'}});
		vizus.cms.dialog.open(url, 0, 0, null, {editor: editor});
	}
	
	tinymce.util.I18n.add('cs',
	{
		'Insert/edit HTML': 'Vložit/změnit HTML',
		'Insert HTML': 'Vložit HTML',
	});

	editor.addButton('vizus_html',
	{
		icon: 'code',
		tooltip: 'Insert/edit HTML',
		onclick: createDialog,
		stateSelector: 'div[data-vizus-cms-component="editor.html"]'
	});

	editor.addMenuItem('vizus_html',
	{
		icon: 'code',
		text: 'Insert HTML',
		onclick: createDialog,
		context: 'insert',
		prependToContext: true,
		stateSelector: 'div[data-vizus-cms-component="editor.html"]'
	});
});
