tinymce.PluginManager.add('vizus_audio', function(editor, url)
{
	function createDialog()
	{
		var url = vizus.url.replace(document.location.toString(), {query: {cmd: 'cms:editor.tinymce.plugins.audio'}});
		vizus.cms.dialog.open(url, 0, 0, null, {editor: editor});
	}
	
	tinymce.util.I18n.add('cs',
	{
		'Insert/edit audio': 'Vložit/změnit audio',
		'Insert audio': 'Vložit audio',
	});

	editor.addButton('vizus_audio',
	{
		icon: 'media',
		tooltip: 'Insert/edit audio',
		onclick: createDialog,
		stateSelector: 'div[data-vizus-cms-component="editor.audio"]'
	});

	editor.addMenuItem('vizus_audio',
	{
		icon: 'media',
		text: 'Insert audio',
		onclick: createDialog,
		context: 'insert',
		prependToContext: true,
		stateSelector: 'div[data-vizus-cms-component="editor.audio"]'
	});
});
