tinymce.PluginManager.add('vizus_video', function(editor, url)
{
	function createDialog()
	{
		var url = vizus.url.replace(document.location.toString(), {query: {cmd: 'cms:editor.tinymce.plugins.video'}});
		vizus.cms.dialog.open(url, 0, 0, null, {editor: editor});
	}
	
	tinymce.util.I18n.add('cs',
	{
		'Insert/edit video': 'Vložit/změnit video',
		'Insert video': 'Vložit video',
	});

	editor.addButton('vizus_video',
	{
		icon: 'media',
		tooltip: 'Insert/edit video',
		onclick: createDialog,
		stateSelector: 'div[data-vizus-cms-component="editor.video"]'
	});

	editor.addMenuItem('vizus_video',
	{
		icon: 'media',
		text: 'Insert video',
		onclick: createDialog,
		context: 'insert',
		prependToContext: true,
		stateSelector: 'div[data-vizus-cms-component="editor.video"]'
	});
});
