tinymce.PluginManager.add('vizus_exec', function(editor, url)
{
	function createDialog()
	{
		var url = vizus.url.replace(document.location.toString(), {query: {cmd: 'cms:editor.tinymce.plugins.exec'}});
		vizus.cms.dialog.open(url, 0, 0, null, {editor: editor});
	}
	
	tinymce.util.I18n.add('cs',
	{
		'Insert/edit function': 'Vložit/změnit funkci',
		'Insert function': 'Vložit funkci',
	});

	editor.addButton('vizus_exec',
	{
		icon: 'code',
		tooltip: 'Insert/edit function',
		onclick: createDialog,
		stateSelector: 'div[data-vizus-cms-component="editor.exec"]'
	});

	editor.addMenuItem('vizus_exec',
	{
		icon: 'code',
		text: 'Insert function',
		onclick: createDialog,
		context: 'insert',
		prependToContext: true,
		stateSelector: 'div[data-vizus-cms-component="editor.exec"]'
	});
});
